  width: 40px;
  height: 40px;
  color: #fff;
  font-weight: bold;
  font-size: 18px;
  cursor: pointer;
  transition: box-shadow 0.2s;
  box-shadow: 0 1px 4px rgba(0,0,0,0.08);
  overflow: hidden;
}

.facepile-initials {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 100%;
  text-decoration: none;
  color: inherit;
}

.facepile-dropdown {
  min-width: 180px;
  padding: 8px 0;
  background: #fff;
  color: #333;
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.12);
}

.facepile-dropdown-item {
  padding: 0;
}

.facepile-dropdown .dropdown-item {
  color: #333;
  padding: 8px 16px;
  cursor: pointer;
  transition: background 0.2s;
}

.facepile-dropdown .dropdown-item:hover {
  background: #f5f5f5;
}

.noTopBorder {
  border-top: 1px solid #eee;
  margin-top: 4px;
}

/* Responsive styles */
@media (max-width: 900px) {
  .header-content {
    padding: 0 10px;
  }
  .header-logo, #logo {
    height: 28px;
  }
  .header-menu li {
    margin-left: 10px;
  }
  .facepile {
    width: 32px;
    height: 32px;
    font-size: 14px;
  }
}

@media (max-width: 600px) {
  .app-header {
    height: 48px;
  }
  .header-content {
    height: 48px;
    padding: 0 4px;
  }
  .header-logo, #logo {
    height: 22px;
  }
  .header-menu li {
    margin-left: 6px;
  }
  .facepile {
    width: 26px;
    height: 26px;
    font-size: 11px;
  }
  .facepile-dropdown {
    min-width: 120px;
    font-size: 13px;
  }
}
